package core.loops;

public class Code3 {

	// 3. for loop
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 * for(intialize; condition; update) {
		 * .....
		 * .....
		 * .....
		 * }
		 */
		int i=10;
		for(;;) {
			System.out.println(i);
			i--;
			if(i==0) break;
		}
		
	}

}
